import './App.css';
import {useState} from "react";
import ModalImage from "react-modal-image";
import axios from "axios";
import SearchField from "react-search-field";
import Pagination from "react-js-pagination";
import 'bootstrap/dist/css/bootstrap.css';

const SearchResults = () => {
    const [activePage, setActivePage] = useState(0);

    return (
        <Pagination
            activePage={activePage}
            itemsCountPerPage={10}
            totalItemsCount={450}
            pageRangeDisplayed={10}
            onChange={setActivePage}
        />
    )
}

const SearchResult = () => {
    const [isOpen, setIsOpen] = useState(false);

    return(
        <div>
            <div className="search-result-container">
                <div className="search-result-info-area">
                    <h2>Title</h2>
                    <p>Description</p>
                </div>
                <ModalImage
                    small={"https://www.artic.edu/iiif/2/1adf2696-8489-499b-cad2-821d7fde4b33/full/400,/0/default.jpg"}
                    large={"https://www.artic.edu/iiif/2/1adf2696-8489-499b-cad2-821d7fde4b33/full/843,/0/default.jpg"}
                    alt="Title"
                />
            </div>
        </div>
    )
}

function getImage(imageID) {
    return "https://www.artic.edu/iiif/2/" + imageID + "/full/843,/0/default.jpg"
}

function getUri(inputText) {
    return "https://api.artic.edu/api/v1/artworks/search?q=" + inputText
}

function App() {
  function sendRequest(inputText) {
      axios({
          method: 'get',
          url: getUri(inputText),
          responseType: 'json'
      }).then(
          response => {
              if(response.status === 200) {
                  console.log('data:', response.data)
              } else {
                  console.log('error:', response)
              }
          }
      )
  }

  return (
    <div className="App">
      <header className="App-header">
          <h1>Art Institute of Chicago Search</h1>
      </header>
        <div className="Body">
            <SearchField
                placeholder="Search art by name, type, keyword, etc..."
                onEnter={(val, event) => sendRequest(val)}
                onSearchClick={(val) => sendRequest(val)}
                searchText=""
                classNames="search-field"
            />
            <SearchResults />
            <SearchResult />
        </div>
    </div>
  );
}

export default App;
