import './App.css';
import Results from "./components/Results";
import {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import SearchBar from "./components/SearchBar";
import axios from "axios";

function App() {
    const [activeSearch, setActiveSearch] = useState('');
    const [activePage, setActivePage] = useState(1);


    function getSearchUri(inputText) {
        return "https://api.artic.edu/api/v1/artworks/search?&fields=id,title,description,image_id&q=" + inputText+ "&page=" + activePage
    }

    axios({
        method: 'get',
        url: getSearchUri(activeSearch, activePage),
        responseType: 'json'
    }).then(
        response => {
            if(response.status === 200) {
                console.log('data:', response)
                setActive(response.data);
            }
        }
    )

    return (
        <div className="App">
            <header className="App-header">
                <h1>Art Institute of Chicago Search</h1>
            </header>
            <div className="Body">
                <SearchBar setActiveSearch={setActiveSearch}
                           setActivePage={setActivePage} />
                {
                    activeSearch && <Results activePage={activePage}
                                             setActivePage={setActivePage}
                                             data={activeSearch} />
                }
            </div>
        </div>
    );
}

export default App;
