import './App.css';
import Results from "./components/Results";
import {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import SearchBar from "./components/SearchBar";
import axios from "axios";

function getSearchUri(inputText) {
    return "https://api.artic.edu/api/v1/artworks/search?&fields=id,title,description,image_id&q=" + inputText
}

function App() {
    const [activePage, setActivePage] = useState(1);
    const [currentQuery, setCurrentQuery] = useState(null);

    axios({
        method: 'get',
        url: getSearchUri(searchText),
        responseType: 'json'
    }).then(
        response => {
            if(response.status === 200) {
                console.log('data:', response)
                props.setCurrentQuery(response.data);
            } else {
                console.log('error:', response)
                props.setCurrentQuery('error:', response);
            }
        }
    )

    return (
        <div className="App">
            <header className="App-header">
                <h1>Art Institute of Chicago Search</h1>
            </header>
            <div className="Body">
                <SearchBar setCurrentQuery={setCurrentQuery} />
                {
                    currentQuery && <Results activePage={activePage}
                                             setActivePage={setActivePage}
                                             data={currentQuery} />
                }
            </div>
        </div>
    );
}

export default App;
