import {Button, Form, InputGroup} from "react-bootstrap";
import {useState} from "react";

const SearchBar = (props) => {
    const [searchText, setSearchText] = useState('');

    const onSearchTextChange = ({ target: { value } }) => {
        setSearchText(value);
    };

    const onSubmitQuery = event => {
        event.preventDefault();
        props.setSearch(searchText);
    }


    return (
        <form onSubmit={onSubmitQuery}>
            <InputGroup className="mb-3">
                <Form.Control
                    placeholder="Search art by name, type, keyword, etc."
                    aria-describedby="basic-addon2"
                    aria-label="Search query terms"
                    value={searchText || ''}
                    onChange={onSearchTextChange}
                />
                <Button variant="success" id="button-addon2" type="submit" className="btn">
                    Search
                </Button>
            </InputGroup>
        </form>
    )
}

export default SearchBar;
