import './App.css';
import axios from "axios";
import Results from "./components/Results";
import Pagination from "react-js-pagination";
import {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {Button, Form, InputGroup} from "react-bootstrap";

function getImage(imageID) {
    return "https://www.artic.edu/iiif/2/" + imageID + "/full/843,/0/default.jpg"
}

function getUri(inputText) {
    return "https://api.artic.edu/api/v1/artworks/search?q=" + inputText
}

function sendRequest(inputText) {
    axios({
        method: 'get',
        url: getUri(inputText),
        responseType: 'json'
    }).then(
        response => {
            if(response.status === 200) {
                console.log('data:', response.data)
            } else {
                console.log('error:', response)
            }
        }
    )
}

function App() {
    const [activePage, setActivePage] = useState(0);

  return (
    <div className="App">
      <header className="App-header">
          <h1>Art Institute of Chicago Search</h1>
      </header>
        <div className="Body">
            <InputGroup className="mb-3">
                <Form.Control
                    placeholder="Search art by name, type, keyword, etc."
                    aria-describedby="basic-addon2"
                    aria-label="Search query terms"
                />
                <Button variant="success" id="button-addon2">
                    Search
                </Button>
            </InputGroup>
            <Results />
            <div className="d-flex align-items-center justify-content-center text-center">
                <Pagination
                    activePage={activePage}
                    totalItemsCount={450}
                    itemsCountPerPage={10}
                    onChange={setActivePage}
                    itemClass="page-item"
                    linkClass="page-link"
                    innerClass="pagination"
                />
            </div>
            <div className="d-flex align-items-center justify-content-center text-center">
            <p>Showing results {(activePage-1)*10} - {activePage*10}</p>
            </div>
        </div>
    </div>
  );
}

export default App;
