import {Button, Form, InputGroup} from "react-bootstrap";
import {useState} from "react";
import '../App.css';
import {useEffect} from "@types/react";
import axios from "axios";

function getSearchUri(inputText) {
    return "https://api.artic.edu/api/v1/artworks/search?q=" + inputText
}

const SearchBar = (props) => {
    const [searchText, setSearchText] = useState('');

    const onSearchTextChange = ({ target: { value } }) => {
        setSearchText(value);
    };

    const onSubmitQuery = event => {
        event.preventDefault();
        props.setSearch(searchText);
    }

    useEffect(() => {
        if(searchText && searchText !== '') {
            axios({
                method: 'get',
                url: getSearchUri(searchText),
                responseType: 'json'
            }).then(
                response => {
                    if(response.status === 200) {
                        console.log('data:', response.data)
                    } else {
                        console.log('error:', response)
                    }
                }
            )
        }
    }, [])

    return (
        <form onSubmit={onSubmitQuery}>
            <InputGroup className="mb-3">
                <Form.Control
                    placeholder="Search art by name, type, keyword, etc."
                    aria-describedby="basic-addon2"
                    aria-label="Search query terms"
                    value={searchText || ''}
                    onChange={onSearchTextChange}
                />
                <Button variant="success" id="button-addon2" type="submit">
                    Search
                </Button>
            </InputGroup>
        </form>
    )
}

export default SearchBar;
