import './App.css';
import Results from "./components/Results";
import {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import SearchBar from "./components/SearchBar";
import axios from "axios";
import {Button} from "react-bootstrap";

function App() {
    const [searchText, setSearchText] = useState('');
    const [page, setPage] = useState(1);
    const [searchResponse, setSearchResponse] = useState();
    const [galleryMode, setGalleryMode] = useState(true);

    const apiUrl = 'https://api.artic.edu/api/v1/artworks/search';

    useEffect(() => {
        window.scrollTo(0, 0)

        axios.get(apiUrl, {
            method: 'get',
            responseType: 'json',
            params: {
                q: searchText,
                page: page,
                fields: 'id,title,description,image_id',
            }
        }).then(response => {
                if(response.status === 200) {
                    console.log('JSON response:', response)
                    setSearchResponse(response.data);
                } else {
                    console.log('The response was an error:', response)
                }
            }
        )
    }, [searchText, page])

    return (
        <div className="App">
            <header className="App-header">
                <h1>Art Institute of Chicago Search</h1>
                <Button size="lg" variant="dark" onClick={() => setGalleryMode(true)}>
                    Gallery View
                </Button>
                <Button size="lg" variant="dark" onClick={() => setGalleryMode(false)}>
                    Reading View
                </Button>
            </header>
            <div className="Body">
                <SearchBar setActiveSearch={setSearchText}
                           setActivePage={setPage} />
                {
                    searchText && <Results galleryMode={galleryMode}
                                           activeSearch={searchText}
                                           page={page}
                                           setPage={setPage}
                                           data={searchResponse} />
                }
            </div>
        </div>
    );
}

export default App;
