import './App.css';
import Results from "./components/Results";
import {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import SearchBar from "./components/SearchBar";
import axios from "axios";

function App() {
    const [activeSearch, setActiveSearch] = useState('');
    const [activePage, setActivePage] = useState(1);
    const [currentQuery, setCurrentQuery] = useState();

    function getSearchUri(inputText) {
        return "https://api.artic.edu/api/v1/artworks/search?&fields=id,title,description,image_id&q=" + inputText+ "&page=" + activePage
    }

    useEffect(() => {
        window.scrollTo(0, 0)

        axios({
            method: 'get',
            url: getSearchUri(activeSearch, activePage),
            responseType: 'json'
        }).then(response => {
                if(response.status === 200) {
                    console.log('JSON response:', response)
                    setCurrentQuery(response.data);
                } else {
                    console.log('The response was an error:', response)
                }
            }
        )
    }, [activeSearch, activePage])

    return (
        <div className="App">
            <header className="App-header">
                <h1>Art Institute of Chicago Search</h1>
            </header>
            <div className="Body">
                <SearchBar setActiveSearch={setActiveSearch}
                           setActivePage={setActivePage} />
                {
                    activeSearch && <Results activeSearch={activeSearch}
                                             activePage={activePage}
                                             setActivePage={setActivePage}
                                             data={currentQuery} />
                }
            </div>
        </div>
    );
}

export default App;
